import React, { Component } from 'react'
import {callApi, e, swr, apiConfigs} from '../../adapter/common'
import { Link } from 'react-router-dom'

import InputField from '../../components/inputField'
import Button from '../../components/button'

import styles from '../../styles/entry/index.module.scss'

class Login extends Component{
    constructor (props){
        super(props)

        this.state = {
            email: '',
            password: '',
            isLoading: false
        }
    }
    componentDidMount(){
        if(localStorage.user){
            localStorage.removeItem('user')
        }
    }
    model(e){
        this.setState({
            [e.name]: e.text
        })
    }

    async login(){
        if(this.state.isLoading === true){
            return
        }
        
        if(this.state.email.trim()==='' || this.state.password.trim()===''){
            return e("Please, ensure you have filled in all fields")
        }
        else{
            this.setState({
                isLoading : true
            })

            const res = await callApi('post', apiConfigs.apiUrl+'user/login.php', this.state)
            if(res.status === 200){
                localStorage.setItem('user', JSON.stringify(res.data))
                this.props.history.push('/dashboard')
            }
            else{
                swr(res.data.message)
            }

            this.setState({
                isLoading : false
            })
        }
    }

    render(){
        return(
            <div className={styles.container}>
                <div className={styles.narrativeSection}>
                    <div>
                        <h2>Host, Manage and Monetize Your Online Classes</h2>
                    </div>
                </div>
                
                <div className={styles.formSection}>
                    <div>
                        <div className={styles.formHeader}>
                            <img src={elpisLogo} alt=""/>
                            <p>Ready? Log In!</p>
                        </div>

                        <div className={styles.formElements}>
                            <InputField type="email" placeholder="Email" name="email" inputValue={ e => this.model(e)} />
                            <InputField type="password" name="password" placeholder="Password" inputValue={ e => this.model(e)} />
                            <div className={styles.forgotPassword}>
                                <span>Forgot Password?</span>
                            </div>
                            <Button onClick={() => this.login()} isLoading={this.state.isLoading} text="Log In"/>
                        </div>

                        <div className={styles.formSwitch}>
                            <p>
                                <span>Don’t have an account? </span>
                                <Link to="/register" className={styles.link}>Sign Up</Link>
                            </p>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}
     

export default SignIn
