// const app_url = 'http://127.0.0.1:3000/'
const app_url = 'https://deesuntech.com.ng/'

export const url = {
	// landingpage
	base: app_url,
	home: '/',
	aboutUs: '/about-us',
	bugReport: '/bug-report',
	portfolio: '/product/porfolio',
	digiStore: '/product/digi-store',
	ourTeam: '/our-team',

	privacy: '/policy-privacy',
	suggestFeature: '/suggest-new-feature',

	//onboard
	signUp: '/onboard/sign-up',
	signIn: '/onboard/sign-in',
	fgtPwd: '/onboard/forgot-password',
	pwdReset: '/onboard/password-reset',
	//
	verifyEmail: '/onboard/verify/:token/:encodedEemail',
	liveVerifyEmail: app_url + 'onboard/verify/',

	//profile page
	home_profile: '/ux/:user',
	contact_profile: '/ux/:user/contact',
	resume_profile: '/ux/:user/resume',
	project_profile: '/ux/:user/project',
	// dashboard
	dash_base: '/dashboard/',
	//1:dashboard home
	dashHome: '/dashboard/home',
	addEditSocial: '/dashboard/home/my-socials',
	//2:project
	projAdd: '/dashboard/project',
	projView: '/dashboard/project/view',
	//3:add
	addView: '/dashboard/add/view',
	addAdd: '/dashboard/add',
	addNewSKill: '/dashboard/add/skills',
	addNewCustomSKill: '/dashboard/add/custom-skills',
	//4: resume
	resumeView: '/dashboard/resume/view',
	// addResume: '/dashboard/resume',
	addWork: '/dashboard/resume/work',
	addEducation: '/dashboard/resume/education',
	//5:setting
	settingView: '/dashboard/settings',
	accountEdit: '/dashboard/account-edit',
	chngePass: '/dashboard/settings/change-password',
}

const remote_url = 'https://portfolio.deesuntech.com.ng/api/v1/'
// const remote_url = 'http://127.0.0.1:8000/api/v1/'
export const remote = {
	//1: education ::  @post @put @delete
	education: remote_url + 'dashboard/educations',
	//2: work
	work: remote_url + 'dashboard/works',
	//3: project
	project: remote_url + 'dashboard/projects',
	//  4: icon
	// 5: user
	addProfileImg: remote_url + 'dashboard/users/changeProfilePics',
	changePass: remote_url + 'dashboard/users/changePassword',
	uploadAvatar: remote_url + 'dashboard/users/changeAvatar',
	onOffProfile: remote_url + 'dashboard/users/publish',
	editProfile: remote_url + 'dashboard/users/personal',
	allowDownloadOfResume: remote_url + 'dashboard/users/allow-resume-download',
	//6: social
	social: remote_url + 'dashboard/socials',
	//7:  onboard
	signUp: remote_url + 'register', //
	activateAcc: remote_url + 'activate',
	fgtPass: remote_url + 'forgot-password',
	rstPass: remote_url + 'password-reset',
	resendEmailVerification: remote_url + '',
	signIn: remote_url + 'login', //
	//8:  skill
	skill: remote_url + 'dashboard/skills',
	skillSingleDelete: remote_url + 'dashboard/skills/delete',
	//9: custom
	custom: remote_url + 'dashboard/customs',
	//10: publicProfile
	// ! public pr
	userProfile: remote_url + 'user/',
	contactMe: remote_url + 'contact-me',
	newFeatureBugReport: remote_url + 'issue-bug-notification',
	publicPDFDownload: remote_url + 'generate-pdf',
}
